import React, {Component} from 'react';
import Grocery from "./Components/Grocery"; 
import { ApolloProvider } from '@apollo/client';
import { ApolloClient, HttpLink, InMemoryCache } from '@apollo/client'; 
import List2 from './Components/List2'; 
//import { render } from 'react-dom';


const client = new ApolloClient(
    {
      cache: new InMemoryCache(),
      link: new HttpLink({
        uri: 'https://crowdburst.herokuapp.com/v1/graphql',
      })
    }
  ); 



class Form2 extends React.Component {



        // <form>


        //     <label htmlFor = "Grocery Store">Add Grocery Store</label>
        //     <input type = "text" name = "Grocery Store" id="Grocery Store"/>
        //     <button>Add</button>
        //     <input type = "submit" value = "Submit"/>
        // </form>

                            // <button className="button is-info">
                            //     Add Grocery Store
                            // </button>



constructor(props)
{
    super(props)
    this.state = 
    {
        list: 
        [
            <ApolloProvider client = {client}>
                <Grocery/>
            </ApolloProvider>

            //"trial"
        ]
    }



    this.addItem = this.addItem.bind(this)
    //this.handleChange = this.handleChange.bind(this)


}

addItem(e) {
  // Prevent button click from submitting form
  e.preventDefault();

  // Create variables for our list, the item to add, and our form
  let list = this.state.list;
  const newItem = document.getElementById("addInput");
  const form = document.getElementById("addItemForm");

  // If our input has a value
  if (newItem.value != "") {
    // Add the new item to the end of our list array
    list.push(newItem.value);
    // Then we use that to set the state for list
    this.setState({
      list: list
    });
    // Finally, we need to reset the form
    newItem.classList.remove("is-danger");
    form.reset();
  } else {
    // If the input doesn't have a value, make the border red since it's required
    newItem.classList.add("is-danger");
  }
}






render() {
    return (
      <div className="content">
        <div className="container">
          <section className="section">
          <List2 items={this.state.list} delete={this.removeItem} />
          </section>
          <hr />
    <section className="section">
      <form className="form" id="addItemForm">
        <input type="text" className="input" id="addInput" placeholder="Grocery Store" />
        <button className="button is-info" onClick={this.addItem}>
          Add Item
        </button>
      </form>
      <form className = "form" id = "addItemForm">
        <input type = "text" className = "input" id="addInput" placeholder="Address"/>
        <button className="button is-info" onClick={this.addItem}>
          Add Address
        </button>
      </form>
    </section>
        </div>
      </div>
    )
  }

}

export default Form2
