import React from 'react';
import {useQuery, gql} from '@apollo/client';
import {List, ListItem} from './shared/List';
import {Badge} from './shared/Badge';

const GROCERY = gql`

query GroceryQuery($id: String!) {
    grocery_by_pk(id: $id) {
      address
      alias
      id
      name
      finrevs {
        body
        grid
      }
    }
  }

`; 

