import styled from '@emotion/styled';

export const Badge = styled.span `
    display: inline-block;
    padding: 0.5rem 1 rem;
    font-size: 1.6rem;
    font-weight: 600;
    line-heigh: 1,
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    border-radius: 0.25rem;
    color #fff;
    background-color: #17a2b8;
`;
