import React, { Component } from "react";
import {
  Stitch,
  AnonymousCredential,
  RemoteMongoClient
} from "mongodb-stitch-browser-sdk";
import "./App.css";

class Stitch2 extends Component {
  constructor() {
    super();
    this.state = {
      todos: [],
      value: ""
    };

    this.handleChange = this.handleChange.bind(this);
    this.displayTodos = this.displayTodos.bind(this);
    this.addTodo = this.addTodo.bind(this);
  }

  componentDidMount() {
    // Initialize the App Client
    this.client = Stitch.initializeDefaultAppClient("crowdburst-ffpqc");
    // Get a MongoDB Service Client, used for logging in and communicating with Stitch
    const mongodb = this.client.getServiceClient(
      RemoteMongoClient.factory,
      "mongodb-atlas"
    );
    // Get a reference to the todo database
    this.db = mongodb.db("todo");
    const itemss = this.mongodb.db('todo').collection('item')
    this.displayTodosOnLoad();
  }

  displayTodos() {
    this.db
      .collection("item")
      .find({}, { limit: 1000 })
      .asArray()
      .then(todos => {
        this.setState({
          todos
        });
      });
  }

  displayTodosOnLoad() {
    this.client.auth
      .loginWithCredential(new AnonymousCredential())
      .then(this.displayTodos)
      .catch(console.error);
  }

  addTodo(event) {
    event.preventDefault();
    const { value } = this.state;

    // this.db
    //   .collection("item")
    //   .insertOne({
    //     owner_id: this.client.auth.user.id,
    //     item: value
    //   })

      itemss.insertOne({
        owner_id: this.client.auth.user.id,
        item: value
      })

      .then(this.displayTodos);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  render() {
    return (
      <div className="App">
        <h3>This is a todo app</h3>
        <hr />
        <p>Add a Todo Item:</p>
        <form onSubmit={this.addTodo}>
          <label>
            <input
              type="text"
              value={this.state.value}
              onChange={this.handleChange}
            />
          </label>
          <input type="submit" value="Submit" />
        </form>
        <ul>
          {this.state.todos.map(todo => {
            return <li>{todo.item}</li>;
          })}
        </ul>
      </div>
    );
  }
}

export default Stitch2;
