import React, {Component} from 'react';
// import {View, Text, SafeAreaView} from 'react-native';
import { 
    Stitch,
    RemoteMongoClient,
    AnonymousCredential
  } from "mongodb-stitch-browser-sdk";



class Stitch2 extends Component {
    constructor() {
      super();
      this.state = {
        reviews: [],
        value: ""
      };

      this.handleChange = this.handleChange.bind(this);
      this.displayTodos = this.displayTodos.bind(this);
      this.addTodo = this.addTodo.bind(this);
    }

    componentDidMount() {
      // Initialize the App Client
      this.client = Stitch.initializeDefaultAppClient("crowdburst-dlnyx");
      // Get a MongoDB Service Client, used for logging in and communicating with Stitch
      const mongodb = this.client.getServiceClient(
        RemoteMongoClient.factory,
        "mongodb-atlas"
      );
      // Get a reference to the todo database
      this.db = mongodb.db("reviews");

      this.displayTodosOnLoad();
    }

    displayTodos() {
      this.db
        .collection("items")
        .find({}, { limit: 1000 })
        .asArray()
        .then(reviews => {
          this.setState({
            reviews
          });
        });
    }

    displayTodosOnLoad() {
      this.client.auth
        .loginWithCredential(new AnonymousCredential())
        .then(this.displayTodos)
        .catch(console.error);
    }

    addTodo(event) {
      event.preventDefault();
      const { value } = this.state;

      this.db
        .collection("items")
        .insertOne({
        owner_id: this.client.auth.user.id,
          items: value
        })
        .then(this.displayTodos)
        .catch(console.error)

    }

    handleChange(event) {
      this.setState({ value: event.target.value });
    }

    render() {
      return (
        <div className="App">
          <h3>This is a todo app</h3>
          <hr />
          <p>Add a Todo Item:</p>
          <form onSubmit={this.addTodo}>
            <label>
              <input
                type="text"
                value={this.state.value}
                onChange={this.handleChange}
              />
            </label>
            <input type="submit" value="Submit" />
          </form>
          <ul>
            {this.state.reviews.map(item => {
              return <li>{item.items}</li>;
            })}
          </ul>
        </div>
      );
    }

}



  export default Stitch2;
