import React, {useState} from 'react';
import {useSubscription, useMutation, gql, useLazyQuery} from '@apollo/client';
import {List, ListItem} from './shared/List';
import {Badge} from './shared/Badge';
import InputForm from "./shared/InputForm";


const GROCERY = gql 
`

    subscription Grocery($id: String!)
    {
        grocery_by_pk(id: $id)
        {
            address
            alias
            display_phone
            id
            name
            finrevs {
                body
                grid
                    }
        }
    }

`;

const Groceries = ({
    match: {
      params: { id },
    },
  }) => {
    const { loading, error, data } = useSubscription(GROCERY, {
      variables: { id },
    });

    if (loading) return <p>Loading ...</p>;
  if (error) return <p>Error :(</p>;


    const { name, address, finrev } = data.grocery_by_pk;

  return (
    <div>
      <h3>
        {name} <Badge>{address}</Badge>
      </h3>


      <List>
        {finrev.map((finrev) => (
          <ListItem key={finrev.id}>{finrev.body}</ListItem>
        ))}
      </List>
    </div>
  );
};

export default Groceries;
