import React, {Component} from 'react';
import Grocery from "./Components/Grocery"; 
import { ApolloProvider } from '@apollo/client';
import { ApolloClient, HttpLink, InMemoryCache } from '@apollo/client'; 

const client = new ApolloClient(
    {
      cache: new InMemoryCache(),
      link: new HttpLink({
        uri: 'https://crowdburst.herokuapp.com/v1/graphql',
      })
    }
  ); 



class Form2 extends React.Component {



        // <form>


        //     <label htmlFor = "Grocery Store">Add Grocery Store</label>
        //     <input type = "text" name = "Grocery Store" id="Grocery Store"/>
        //     <button>Add</button>
        //     <input type = "submit" value = "Submit"/>
        // </form>

                            // <button className="button is-info">
                            //     Add Grocery Store
                            // </button>



constructor(props)
{
    super(props)
    this.state = 
    {
        list: 
        [
            <ApolloProvider client = {client}>
                <Grocery/>
            </ApolloProvider>
            //"trial"
        ]
    }

    this.addItem = this.addItem.bind(this)
    this.removeItem = this.removeItem.bind(this)
    this.handleChange = this.handleChange.bind(this)


}

addItem(e) {
  // Prevent button click from submitting form
  e.preventDefault();

  // Create variables for our list, the item to add, and our form
  let list = this.state.list;
  const newItem = document.getElementById("addInput");
  const form = document.getElementById("addItemForm");

  // If our input has a value
  if (newItem.value != "") {
    // Add the new item to the end of our list array
    list.push(newItem.value);
    // Then we use that to set the state for list
    this.setState({
      list: list
    });
    // Finally, we need to reset the form
    newItem.classList.remove("is-danger");
    form.reset();
  } else {
    // If the input doesn't have a value, make the border red since it's required
    newItem.classList.add("is-danger");
  }
}

removeItem(item) {
  // Put our list into an array
  const list = this.state.list.slice();
  // Check to see if item passed in matches item in array
  list.some((el, i) => {
    if (el === item) {
      // If item matches, remove it from array
      list.splice(i, 1);
      return true;
    }
  });
  // Set state to list
  this.setState({
    list: list
  });
}

handleChange(e) {
  // Variable to hold the original version of the list
  let currentList = [];
  // Variable to hold the filtered list before putting into state
  let newList = [];

  // If the search bar isn't empty
  if (e.target.value !== "") {
    // Assign the original list to currentList
    currentList = this.props.items;

    // Use .filter() to determine which items should be displayed
    // based on the search terms
    newList = currentList.filter(item => {
      // change current item to lowercase
      const lc = item.toLowerCase();
      // change search term to lowercase
      const filter = e.target.value.toLowerCase();
      // check to see if the current list item includes the search term
      // If it does, it will be added to newList. Using lowercase eliminates
      // issues with capitalization in search terms and search content
      return lc.includes(filter);
    });
  } else {
    // If the search bar is empty, set newList to original task list
    newList = this.props.items;
  }
  // Set the filtered state based on what our rules added to newList
  this.setState({
    filtered: newList
  });
}


render() {
    return (
      <div className="content">
        <div className="container">
        <input type="text" className="input" onChange={this.handleChange} placeholder="Search..." />

          <section className="section">

              {this.state.list.map(item => (
                <li key={item}>{item}</li>
              ))}

          </section>

          <hr />
    <section className="section">
      <form className="form" id="addItemForm">
        <input type="text" className="input" id="addInput" placeholder="Grocery Store" />
        <button className="button is-info" onClick={this.addItem}>
          Add Item
        </button>
      </form>
      <form className = "form" id = "addItemForm">
        <input type = "text" className = "input" id="addInput" placeholder="Address"/>
        <button className="button is-info" onClick={this.addItem}>
          Add Address
        </button>
      </form>
    </section>
        </div>
      </div>
    )
  }

}

export default Form2
